import React from "react";

const Message = ({ msg }) => {
  return (
    <div className={`msg ${msg.role}`}>
      <p>{msg.text}</p>
      {msg.sources && msg.sources.length > 0 && (
        <ul>
          {msg.sources.map((src, i) => (
            <li key={i}>
              <a href={src} target="_blank" rel="noreferrer">{src}</a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Message;
